-- -----------------------------------------------------------
-- VERIFICATION
-- -----------------------------------------------------------
SELECT 'clinics'       AS tbl, COUNT(*) AS rows FROM clinics
UNION ALL SELECT 'clinic_config',  COUNT(*) FROM clinic_config
UNION ALL SELECT 'doctors',        COUNT(*) FROM doctors
UNION ALL SELECT 'doctor_config',  COUNT(*) FROM doctor_config
UNION ALL SELECT 'patients',       COUNT(*) FROM patients
UNION ALL SELECT 'appointments',   COUNT(*) FROM appointments
UNION ALL SELECT 'tasks',          COUNT(*) FROM tasks;

-- Appointments breakdown per doctor
SELECT
  c.name                                  AS clinic,
  d.first_name || ' ' || d.last_name     AS doctor,
  COUNT(*)                                AS total,
  COUNT(*) FILTER (WHERE a.status = 'scheduled')  AS scheduled,
  COUNT(*) FILTER (WHERE a.status = 'completed')  AS completed,
  COUNT(*) FILTER (WHERE a.status = 'cancelled')  AS cancelled,
  COUNT(*) FILTER (WHERE a.status = 'no_show')    AS no_show
FROM clinics c
JOIN doctors d ON d.clinic_id = c.id
JOIN appointments a ON a.doctor_id = d.id
GROUP BY c.name, d.first_name, d.last_name
ORDER BY c.name, doctor;

-- Tasks breakdown per doctor
SELECT
  c.name                              AS clinic,
  d.first_name || ' ' || d.last_name AS doctor,
  COUNT(*) FILTER (WHERE t.status='pending')   AS pending_tasks,
  COUNT(*) FILTER (WHERE t.status='completed') AS completed_tasks
FROM clinics c
JOIN doctors d ON d.clinic_id = c.id
JOIN tasks   t ON t.doctor_id = d.id
GROUP BY c.name, d.first_name, d.last_name
ORDER BY c.name, doctor;
